script_properties('work-in-pause')local samp = require('samp.events')local effil = require('effil')local inicfg = require('inicfg')local ffi = require('ffi')local imgui = require('mimgui')local encoding = require('encoding')encoding.default = 'CP1251'local u8 = encoding.UTF8-- ================= НАСТРОЙКИ ОБНОВЛЕНИЯ =================local SCRIPT_VERSION = "0.0.1" local UPDATE_URL = "https://github.com/dmashmakov2000-coder/item11/raw/refs/heads/main/Item.lua"-- ========================================================local SCRIPT_CONFIG_NAME = 'Item'local SCRIPT_CONFIG_FILENAME = SCRIPT_CONFIG_NAME .. '.ini'local items_name = {    -- рулетки 4/4    [555] = "Бронзовая рулетка",    [556] = "Серебряная рулетка",    [557] = "Золотая рулетка",    [1425] = "Платиновая рулетка",    -- Ларцы    [7480] = "Ларец Fortnite",    [4794] = "Ларец Кладоискателя",    [1769] = "Супер Мото-ящик",    [1639] = "Rare box Blue",    [1637] = "Rare box Yellow",    [1638] = "Rare box Red",    -- Дроп    [1811] = "Bitcoin (BTC)",    [522] = "Семейный талон",    [4344] = "Талон +1 EXP",    [5991] = "Грунт",    -- Бонусы    [9697] = "Монета Нового года (2026)",    [1146] = "Гражданский талон",    [731] = "Аz-Coins",    [9726] = "Лотерейный билет 2к26",}local cfg = inicfg.load({    config = {        chat = '',        token = '',        itemAdding = false    }}, SCRIPT_CONFIG_NAME)local chat = imgui.new.char[128](tostring(cfg.config.chat))local token = imgui.new.char[128](tostring(cfg.config.token))local itemAdding = imgui.new.bool(cfg.config.itemAdding)local window = imgui.new.bool(false)-- Поток для работы с сетьюlocal networkThread = effil.thread(function(url, mode, data)    local requests = require('requests')    if mode == "telegram" then        return requests.post(url, {params = data})    elseif mode == "check_update" then        local res = requests.get(url)        if res and res.status_code == 200 then            return res.text        end    end    return nilend)function main()    while not isSampAvailable() do wait(0) end        sampAddChatMessage('{3083ff}[ItemLog] {ffffff}Скрипт запущен. Версия: ' .. SCRIPT_VERSION .. '. Активация: /item', -1)        sampRegisterChatCommand('item', function() window[0] = not window[0] end)    sampRegisterChatCommand('itemupdate', function() checkUpdate(true) end)    checkUpdate(false)    while true do        wait(0)    endendfunction checkUpdate(manual)    if manual then sampAddChatMessage("[ItemLog] {ffffff}Проверка обновлений...", -1) end        local proc = networkThread(UPDATE_URL, "check_update")    lua_thread.create(function()        while proc:status() == "running" do wait(0) end        local result = proc:get()        if result then            local remote_version = result:match('local SCRIPT_VERSION = "(.-)"')            if remote_version and remote_version ~= SCRIPT_VERSION then                sampAddChatMessage(string.format('[ItemLog] {ffff00}Найдена новая версия: %s. {ffffff}Обновляю...', remote_version), -1)                updateScript(result)            else                if manual then sampAddChatMessage("[ItemLog] {00ff00}У вас установлена последняя версия.", -1) end            end        else            if manual then sampAddChatMessage("[ItemLog] {ff0000}Ошибка при подключении к серверу обновлений.", -1) end        end    end)endfunction updateScript(content)    local f = io.open(thisScript().path, "w")    if f then        f:write(content)        f:close()        sampAddChatMessage("[ItemLog] {00ff00}Обновление успешно! Перезагрузка...", -1)        thisScript():reload()    else        sampAddChatMessage("[ItemLog] {ff0000}Ошибка доступа к файлу!", -1)    endendimgui.OnInitialize(function()    imgui.GetIO().IniFilename = nilend)imgui.OnFrame(    function() return window[0] end,    function(player)        local resX, resY = getScreenResolution()        imgui.SetNextWindowPos(imgui.ImVec2(resX / 2, resY / 2), imgui.Cond.FirstUseEver, imgui.ImVec2(0.5, 0.5))        imgui.SetNextWindowSize(imgui.ImVec2(350, 200), imgui.Cond.FirstUseEver)        imgui.Begin('ItemLog v' .. SCRIPT_VERSION, window, imgui.WindowFlags.NoResize + imgui.WindowFlags.NoCollapse)                if imgui.InputText(u8('ID Чата'), chat, ffi.sizeof(chat)) then            cfg.config.chat = ffi.string(chat)            inicfg.save(cfg, SCRIPT_CONFIG_FILENAME)        end        if imgui.InputText(u8('Токен Бота'), token, ffi.sizeof(token), imgui.InputTextFlags.Password) then            cfg.config.token = ffi.string(token)            inicfg.save(cfg, SCRIPT_CONFIG_FILENAME)        end        if imgui.Checkbox(u8('Уведомлять о предметах'), itemAdding) then            cfg.config.itemAdding = itemAdding[0]            inicfg.save(cfg, SCRIPT_CONFIG_FILENAME)        end                imgui.Separator()        if imgui.Button(u8("Проверить обновления"), imgui.ImVec2(-1, 25)) then            checkUpdate(true)        end                imgui.End()    end)function sendTelegramMessage(text)    local chat_id_str = ffi.string(chat)    local token_str = ffi.string(token)    if chat_id_str == '' or token_str == '' then return end    local text_to_send = text:gsub('{......}', '')     networkThread(('https://api.telegram.org/bot%s/sendMessage'):format(token_str), "telegram", {        chat_id = chat_id_str,        text = u8:encode(text_to_send)    })endfunction samp.onServerMessage(color, text)    if color == -65281 and itemAdding[0] then        -- Поиск ID предмета в формате :itemID:        local itemId = text:match(":item(%d+):")        if itemId then            itemId = tonumber(itemId)            if items_name[itemId] then                sendTelegramMessage("Получен предмет: " .. items_name[itemId])            else                sendTelegramMessage("Получен неизвестный предмет. ID: " .. itemId)            end        end    endend